import isNil from 'lodash/isNil';
import { KEY_TYPE } from '../makeActionCreator/constants';
import makeActionCreator from '../makeActionCreator';

class Vita {
  /**
   * @param {object} objDefaultReducerState - Default reducer state to be
   * returned if attempting to reduce with a null or undefined state.
   */
  constructor(objDefaultReducerState = {}) {
    /**
     * @private
     * @type {?object}
     */
    this._objDefaultReducerState = objDefaultReducerState;

    /**
     * @private
     * @type {Map}
     */
    this._mapActionCreators = new Map();

    /**
     * @private
     * @type {Map}
     */
    this._mapReducerFunctions = new Map();
  }

  /**
   * Clear all registered action creators.
   *
   * @returns {Vita} This.
   */
  clearAllActionCreators = () => {
    this._mapActionCreators.clear();
    return this;
  };

  /**
   * Clear all registered reducers for handling actions.
   *
   * @returns {Vita} This.
   */
  clearAllReducers = () => {
    this._mapReducerFunctions.clear();
    return this;
  }

  /**
   * Gets dispatchable Redux action object for a given action type.
   *
   * @param {string} strActionType - Action type to generate dispatchable
   * object for.
   * @param {...*} varargsActionCreator - Arguments to pass to the registered
   * function.
   * @returns {object} Action object.
   */
  getDispatchable = (strActionType, ...varargsActionCreator) => {
    const ufuncActionCreator = this._mapActionCreators.get(strActionType);

    if (isNil(ufuncActionCreator)) {
      throw new Error(`No action creator was registered for type '${strActionType}'`);
    }

    return ufuncActionCreator(...varargsActionCreator);
  };

  /**
   * Reducer function.
   *
   * @param {object} [uobjCurrentReducerState] - Current reducer state.
   * @param {object} objAction - Occurring Redux action.
   * @returns {object} New reducer state.
   */
  reduce = (uobjCurrentReducerState, objAction) => {
    const objCurrentReducerState = isNil(uobjCurrentReducerState) ?
      this._objDefaultReducerState :
      uobjCurrentReducerState;

    /*
     * We shouldn't have to error check here since all valid Redux actions are
     * required to have a 'type' property. See:
     * https://redux.js.org/basics/actions/
     */
    const { [KEY_TYPE]: strTypeForAction } = objAction;

    const ufuncReducerForAction = this._mapReducerFunctions.get(strTypeForAction);

    // If we have no registered handler, just fallback to current state
    if (isNil(ufuncReducerForAction)) {
      return objCurrentReducerState;
    }

    return ufuncReducerForAction(objCurrentReducerState, objAction);
  };

  /**
   * Create and register an action creator function.
   *
   * @param {string} strActionType - Action type.
   * @param {Function} funcActionCreator - Action creator function.
   * @returns {Vita} This.
   */
  registerActionCreator = (strActionType, funcActionCreator) => {
    this._mapActionCreators.set(strActionType, funcActionCreator);
    return this;
  }

  /**
   * Register a reducer function to handle on receiving an action with a
   * given type.
   *
   * @param {string} strActionType - Action type to invoke reducer function
   * on.
   * @param {Function} funcReducer - Reducer function to invoke on reducing
   * action with type.
   * @returns {Vita} This.
   */
  registerReducer = (strActionType, funcReducer) => {
    this._mapReducerFunctions.set(strActionType, funcReducer);
    return this;
  }

  /**
   * Register an action creator and reducer function at the same time. Note
   * that the action creator param is optional. Omitting it will generate
   * a default action creator.
   *
   * @param {string} strActionType - Action type.
   * @param {Function} funcReducer - Reducer function to invoke on reducing
   * action with type.
   * @param {Function} [ufuncActionCreator] - Action creator function.
   * @returns {Vita} This.
   */
  registerSlice = (strActionType, funcReducer, ufuncActionCreator) => {
    const funcActionCreator = isNil(ufuncActionCreator) ?
      makeActionCreator(strActionType) :
      ufuncActionCreator;

    this.registerActionCreator(strActionType, funcActionCreator);
    this.registerReducer(strActionType, funcReducer);
    return this;
  }

  /**
   * Unregister an action creator for a given action type.
   *
   * @param {string} strActionType - Action type to unregister action creator
   * function for.
   * @returns {Vita} This.
   */
  unregisterActionCreator = (strActionType) => {
    this._mapActionCreators.delete(strActionType);
    return this;
  }

  /**
   * Unregister a reducer function for a given action type.
   *
   * @param {string} strActionType - Action type to unregister handling reducer
   * function for.
   * @returns {Vita} This.
   */
  unregisterReducer = (strActionType) => {
    this._mapReducerFunctions.delete(strActionType);
    return this;
  }

  /**
   * Unregister an action creator and reducer at the same time.
   *
   * @param {string} strActionType - Action type to unregister.
   * @returns {Vita} This.
   */
  unregisterSlice = (strActionType) => {
    this.unregisterActionCreator(strActionType);
    this.unregisterReducer(strActionType);
    return this;
  }
}

export default Vita;

export { default as makeActionCreator } from './makeActionCreator';
export { default as reducerDecrementField } from './reducerDecrementField';
export { default as reducerIncrementField } from './reducerIncrementField';
export { default as reducerRemoveField } from './reducerRemoveField';
export { default as reducerSetField } from './reducerSetField';
export { default as reducerSetManyFields } from './reducerSetManyFields';
export { default as reducerToggleBooleanField } from './reducerToggleBooleanField';
export { default as Vita } from './Vita';

import isEmpty from 'lodash/isEmpty';
import isNil from 'lodash/isNil';
import isPlainObject from 'lodash/isPlainObject';
import { KEY_TYPE } from './constants';

/**
 * @param {string} strType - Action type.
 * @param {Function} [ufuncPropertyCreator] - Optional function to
 * generate additional properties to the action.
 * @returns {Function} Function that creates action objects.
 * @throws {Error} If action type is empty, or additional generated properties
 * were not a plain object.
 */
export default (strType, ufuncPropertyCreator) => (...varargsPropertyCreator) => {
  if (isEmpty(strType)) {
    throw new Error('Type must not be empty.');
  }

  const objAdditionalProperties = isNil(ufuncPropertyCreator) ?
    {} :
    ufuncPropertyCreator(...varargsPropertyCreator);

  /*
   * Sanity-check our property creator function actually generated a plain
   * object (otherwise we'll run into issues w/ Redux later...)
   */
  if (!isPlainObject(objAdditionalProperties)) {
    throw new Error(
      `Property creator function generated an invalid type. Expected plain \
object, got '${typeof objAdditionalProperties}'`,
    );
  }

  return {
    ...objAdditionalProperties,
    [KEY_TYPE]: strType,
  };
};

import has from 'lodash/has';

/**
 * Reducer function for decrementing a numeric field.
 *
 * @param {object} objReducerState - Current reducer state.
 * @param {string} strFieldName - Field to decrement.
 * @returns {object} Reducer state after decrementing field.
 * @throws {Error} If field does not exists.
 * @throws {TypeError} If field is not a number.
 */
export default (objReducerState, strFieldName) => {
  if (!has(objReducerState, strFieldName)) {
    throw new Error(`Field '${strFieldName}' does not exist on current state, and hence cannot be decremented.`);
  }

  const mixedFieldValue = objReducerState[strFieldName];

  if ('number' !== typeof mixedFieldValue) {
    throw new TypeError(
      `Invalid type for decrementing field '${strFieldName}'. Expected 'number', got '${typeof mixedFieldValue}'`,
    );
  }

  return {
    ...objReducerState,
    [strFieldName]: mixedFieldValue - 1,
  };
};

import has from 'lodash/has';

/**
 * Reducer function for incrementing a numeric field.
 *
 * @param {object} objReducerState - Current reducer state.
 * @param {string} strFieldName - Field to increment.
 * @returns {object} Reducer state after incrementing field.
 * @throws {Error} If field does not exists.
 * @throws {TypeError} If field is not a number.
 */
export default (objReducerState, strFieldName) => {
  if (!has(objReducerState, strFieldName)) {
    throw new Error(`Field '${strFieldName}' does not exist on current state, and hence cannot be incremented.`);
  }

  const mixedFieldValue = objReducerState[strFieldName];

  if ('number' !== typeof mixedFieldValue) {
    throw new TypeError(
      `Invalid type for incrementing field '${strFieldName}'. Expected 'number', got '${typeof mixedFieldValue}'`,
    );
  }

  return {
    ...objReducerState,
    [strFieldName]: mixedFieldValue + 1,
  };
};

/**
 * Reducer function for removing a field.
 *
 * @param {object} objReducerState - Current reducer state.
 * @param {string} strFieldName - Field name to remove.
 * @returns {object} Reducer state after removing field.
 */
export default (objReducerState, strFieldName) => {
  const {
    [strFieldName]: mixedFieldToRemove,
    ...objReducerStateWithoutRemovedField
  } = objReducerState;

  return objReducerStateWithoutRemovedField;
};

/**
 * Reducer function for setting a field.
 *
 * @param {object} objReducerState - Current reducer state.
 * @param {string} strFieldName - Field name to set.
 * @param {*} mixedFieldValue - Field value to set.
 * @returns {object} Reducer state after setting field.
 */
export default (objReducerState, strFieldName, mixedFieldValue) => ({
  ...objReducerState,
  [strFieldName]: mixedFieldValue,
});

/**
 * Reducer function for setting many fields.
 *
 * @param {object} objReducerState - Current reducer state.
 * @param {object} objFieldsToSet - Fields to set.
 * @returns {object} Reducer state after setting fields.
 */
export default (objReducerState, objFieldsToSet) => ({
  ...objReducerState,
  ...objFieldsToSet,
});

import has from 'lodash/has';

/**
 * Reducer function for toggling a boolean field.
 *
 * @param {object} objReducerState - Current reducer state.
 * @param {string} strFieldName - Field to toggle.
 * @returns {object} Reducer state after toggling field.
 * @throws {Error} If field does not exists.
 * @throws {TypeError} If field is not a boolean.
 */
export default (objReducerState, strFieldName) => {
  if (!has(objReducerState, strFieldName)) {
    throw new Error(`Field '${strFieldName}' does not exist on current state, and hence cannot be toggled.`);
  }

  const mixedFieldValue = objReducerState[strFieldName];

  if ('boolean' !== typeof mixedFieldValue) {
    throw new TypeError(
      `Invalid type for toggling field '${strFieldName}'. Expected 'boolean', got '${typeof mixedFieldValue}'`,
    );
  }

  return {
    ...objReducerState,
    [strFieldName]: !mixedFieldValue,
  };
};

# redux-vita

[![npm version](https://badge.fury.io/js/redux-vita.svg)](https://badge.fury.io/js/redux-vita)
[![Build Status](https://travis-ci.com/jsau-/redux-vita.svg?branch=master)](https://travis-ci.com/jsau-/redux-vita)

## Installing

`npm install redux-vita`

## Using the Library

Find basic snippets below.

For full API documentation check out the
[documentation page](https://jsau-.github.io/redux-vita).

For examples check out the `examples` directory.

#### Instantiating `Vita`
```
import { Vita } from 'redux-vita';

// Default instantiation (with no default reducer state)
const vita = new Vita();

// Instantiating with default reducer state
const default_reducer_state = { field_one: 'default' };
const vitaWithDefaultState = new Vita(default_reducer_state);
```

#### Registering an Action Creator Function
You can register action creators on a `Vita`, allowing you to create a
dispatchable Redux action object by just passing the action's type at a later
time.

Additionally, when defining an action creator you can additionally pass a
second parameter, which is a function for generating additional properties on
the generated action object.

```
import { Vita, makeActionCreator } from 'redux-vita';

const vita = new Vita();

// Action creator which has no additional fields
const actionCreator = makeActionCreator('action_type');
vita.registerActionCreator('action_type', actionCreator);

// Action creator which accepts some params
const actionCreatorWithParams = makeActionCreator('action_wparams', (argone, argtwo) => ({ argone, argtwo }));
vita.registerActionCreator('action_params', actionCreatorWithParams);
```

#### Getting a Dispatchable Object for an Action Creator
Generate a Redux action object for a registered action type. Note that any
subsequent parameters after the type are provided as parameters to the property
generator function (if provided when registering the creator).

```
// Returns { type: 'action_type' }
const objectAction = vita.getDispatchable('action_type');

// Returns { argone: 1, argtwo: 2 }
const objectAction = vita.getDispatchable('action_wparams', 1, 2);
```

#### Registering a Reducer Function
You can register reducer functions to handle given action types. If no matching
handler is found on invoking `Vita::reduce` then the current state is
returned. If no current state is set, the default reducer state is returned.

```
vita.registerReducer('increment_counter', (state) => ({ ...state, counter: state.counter + 1 }));
```

Note that the library exposes some standard reducer functions that may be useful
in your applications. For a full list of available reducer functions see the
[documentation page](https://jsau-.github.io/redux-vita).

```
import { reducerDecrementField } from 'redux-vita';
vita.registerReducer('decrement_counter', (state) => reducerDecrementField(state, 'counter'));

import { reducerIncrementField } from 'redux-vita';
vita.registerReducer('increment_counter', (state) => reducerIncrementField(state, 'counter'));

import { reducerRemoveField } from 'redux-vita';
vita.registerReducer('clear_counter', (state) => reducerClearField(state, 'counter'));

import { reducerSetField } from 'redux-vita';
vita.registerReducer('maximise_counter', (state) => reducerSetField(state, 'counter', MAX_COUNTER_VALUE));

import { reducerSetManyFields } from 'redux-vita';
vita.registerReducer('reset_form', (state) => reducerSetManyFields(state, { field_one: 'default_one', field_two: 'default_two' }));

import { reducerToggleBooleanField } from 'redux-vita';
vita.registerReducer('toggle_counter_enabled', (state) => reducerToggleBooleanField(state, 'counter_enabled'));
```

#### Registering a Slice
It's possible to register an action creator and reducer function at the same
time using `Vita::registerSlice`. Note that the action creator param is
optional, and will default to `makeActionCreator(ACTION_TYPE)`.

```
// Without specifying an action creator
vita.registerSlice(
  'action_type',
  (state) => reducerClearField(state, 'field_to_clear'),
);

// Specifying an action creator
vita.registerSlice(
  'action_type',
  (state) => reducerClearField(state, 'field_to_clear'),
  makeActionCreator('action_type'),
);
```

#### Using the Reducer Within Redux
Instances of `Vita` expose a function `reduce`. This can be registered as
a reducer in Redux as you would a normal reducer function.

```
combineReducers({ my_reducer_name: vita.reduce });
```

